INSERT INTO `map_dbc_base` VALUES (489, 'PVPZone03', 3, 1, 1, '', '', '', '', '', '戰歌峽谷', '', '', '', '', '', '', '', '', '', '', 16712190, 3277, '', '', '', '', '', '在位於梣谷森林和貧瘠之地交界處的戰歌峽谷中，聯盟和部落之間的戰爭一直在激烈地進行著。\r\n\r\n獸人在這裡建立了戰歌伐木場，不斷砍伐梣谷森林的樹木，這使得銀翼要塞的夜精靈非常憤怒。\r\n\r\n為了幫助自己的盟友取得勝利，聯盟和部落的勇士不斷加入這片戰場，用自己的劍和魔法與敵人展開廝殺。', '', '', '', '', '', '', '', '', '', '', 16712190, '', '', '', '', '', '在位於梣谷森林和貧瘠之地交界處的戰歌峽谷中，聯盟和部落之間的戰爭一直在激烈地進行著。\r\n\r\n獸人在這裡建立了戰歌伐木場，不斷砍伐梣谷森林的樹木，這使得銀翼要塞的夜精靈非常憤怒。\r\n\r\n為了幫助自己的盟友取得勝利，聯盟和部落的勇士不斷加入這片戰場，用自己的劍和魔法與敵人展開廝殺。', '', '', '', '', '', '', '', '', '', '', 16712190, 122, 1, 0, 0, 0, -1, 0, 0, 0);
